USE SCHOOL;
IF EXISTS(SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES
      WHERE TABLE_NAME = 'CurrentContent')
   DROP TABLE CurrentContent
GO

CREATE TABLE CurrentContent
(Id integer primary key identity(1000,1), 
 SNo varchar(50),
 Serial varchar(50),
 Issue varchar(40),
 Special varchar(200),
 Title varchar(400),
 Author varchar(200),
 Page varchar(50),
 Note varchar(4000));

